mapper.select:
select ,C.description as role_name from t_channel_admin A left join t_channel_role_user B on A.id = B.account_id left join t_channel_role C on B.role_id = C.id
order by
${orderBy}

mapper.insert:

insert into t_channel_admin (id, user_name, password,email, phone, short_name,channel_code,agent_no,merchant_no,identity_no,
salt, status,identity_flag, create_time,open_id
)
values (#{id,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR},
#{password,jdbcType=VARCHAR},#{email,jdbcType=VARCHAR}, #{phone}, #{shortName},#{channelCode},#{agentNo},#{merchantNo},#{identityNo},
#{salt,jdbcType=VARCHAR}, #{status,jdbcType=BIT}, #{identityFlag}, #{createTime,jdbcType=TIMESTAMP}, #{openId,jdbcType=VARCHAR}
)

mapper.select:
select

from t_channel_admin A 
where A.merchant_no = #{merchantNo}
and A.identity_flag = #{identityFlag}
and A.open_id is not null
and A.open_id!=''

select

from t_channel_admin A 
where A.merchant_no = #{merchantNo}
and A.identity_flag = #{identityFlag}
and A.open_id is not null
and A.open_id!=A.id

select
from t_channel_admin A LEFT JOIN t_merchant_info B on A.merchant_no = B.merchant_no WHERE B.authen_status = '0' and B.`status` = '6' and A.id != A.open_id and A.channel_code = #{channelNo} and A.create_time <= str_to_date(#{createTime},'%Y-%m-%d %H:%i:%s')
and A.agent_no = #{agentNo}
