mapper.select:
select

from t_merchant_cost A
where A.id = #{id}

			  
			  select ifnull(sum(amount),0) as A_amount,
			  ifnull(sum(merchant_cost),0) as A_merchant_cost,
			  pay_code as A_pay_code,
			  type as A_type
			  from t_merchant_cost A
			  where 1=1
			  		  
			 
				and A.id = #{id}
			
			
				and A.trade_no = #{tradeNo}
			
			
				and A.channel_no = #{channelNo}
			
			
				and A.agent_no = #{agentNo}
			
			
				and A.agent_level = #{agentLevel}
			
			
				and A.merchant_no = #{merchantNo}
			
			
				and A.qr_code = #{qrCode}
			
			
				and A.pay_code = #{payCode}
			
			
				and A.type = #{type}
			
			
				and A.rate_type = #{rateType}
			
			
				and A.amount = #{amount}
			
			
				and A.merchant_rate = #{merchantRate}
			
			
				and A.fixed_amount = #{fixedAmount}
			
			
				and A.merchant_cost = #{merchantCost}
			
			
				and A.status = #{status}
			
			
				and A.create_time = #{createTime}
			
			
        		
        			and A.create_time >= #{beginTimeStr}
        		
      		
        	
	        	
					and A.create_time <= #{endTimeStr}
				
      		
			
				and A.remark = #{remark}
			
			
				and A.temp_1 = #{temp1}
			
			
				and A.temp_2 = #{temp2}
			
      		GROUP BY pay_code ,type
