mapper.select:
select
from t_order_pay A
order by ${orderBy}

select 

from t_order_pay A 
where A.id = #{id}

select ifnull(sum(order_amt),0) as A_order_amt,trade_type as A_trade_type from t_order_pay A where begin_time like CONCAT(#{date},'%') 

and cashier_no=#{cashierNo}


and merchant_no=#{merchantNo}

and pay_status='02'
group by trade_type

select sum(ifnull(order_amt,0)) - ifnull(t.amt, 0) as balance ,pay_code from t_order_pay LEFT JOIN ( SELECT sum(order_amt) AS amt, pay_code AS paycode FROM t_order_pay WHERE update_time like CONCAT(#{date},'%') AND pay_status = '02' AND trade_type = '01'
and cashier_no=#{cashierNo}
and merchant_no=#{merchantNo}
GROUP BY pay_code ) t ON pay_code = t.paycode where begin_time like CONCAT(#{date},'%') and pay_status='02' and trade_type='02'
and cashier_no=#{cashierNo}
and merchant_no=#{merchantNo}
GROUP BY pay_code

   select sum(ifnull(order_amt,0)) total,sum(ifnull(order_amt,0)-IFNULL(merchant_cost,0)) as balance 
    from t_order_pay where begin_time like CONCAT(#{date},'%') and pay_status='02'  and trade_type='02'
    
    and cashier_no=#{cashierNo}
    
    
    and merchant_no=#{merchantNo}

  	select IFNULL(SUM(order_amt),0) from t_order_pay where  
	pay_status = '02' AND trade_type = '02'
	
	   	and cashier_no=#{cashierNo}
    
    
    	and merchant_no=#{merchantNo}
    
	and update_time like CONCAT(#{date},'%');
